import React  from 'react';
import moment from 'moment';

function Time() {
  let current_time = moment().format("h:MM");
  const date = new Date()
  const hours = date.getHours()
  let timeOfDay

  if (hours <12) {
    timeOfDay = "morning"
  } else if(hours >=12 && hours <17) {
    timeOfDay = "afternoon"
  } else {
    timeOfDay = "evening"
  }



  return(
    <div className="time">
      <h1>{current_time}</h1>
      <h3 className="statement">Good {timeOfDay}!</h3>
    </div>
  )
}

export default Time
